import javax.swing.JOptionPane; public class Principal {public static void main (String[] args){int a = Integer.parseInt(JOptionPane.showInputDialog(null,"Introduzca numero de parada"+""));int b = 0;int[] c =  new int[a];while((a)!=b){c [b] = fibonacci(b);JOptionPane.showMessageDialog(null,c[b]+"");b ++;}String d = "["+c[0];for(int i = 1;(i)!=a;i ++){d = ""+d+","+c[i] ;}d = ""+d+"]" ;JOptionPane.showMessageDialog(null,d+"");}public static int fibonacci (int a){if((a)==0){return 0;}else{}if((a)==1){return 1;}else{}return (fibonacci((a)-1))+fibonacci((a)-2);}}